using System.Collections; using System.Collections.Generic; using UnityEngine; using UnityEngine.UI;   public class GameOverPopUp : MonoBehaviour {  //	public AudioSource audioSource; 	 	[SerializeField] private GameObject GameOverPanel; 	public float timeLeft;   	void Start () { 		StartCoroutine("PopUpTime"); 		 	}  	void Update () { 		if (timeLeft <= 0) { 			StopCoroutine ("PopUpTime"); 			GameOverPanel.gameObject.SetActive (true); 			Time.timeScale = 0f; 		} 	}  	IEnumerator	PopUpTime() { 		while (true) { 			yield return new WaitForSecondsRealtime(1); 			timeLeft--; 			} 	}  } 